defs.script:
function toggleItem(item1, item2) {
    var elem2 = document.querySelector("#" + item2);
    elem2.classList.toggle("box2Clicked");
    var elem = document.querySelector("#" + item1);
    elem.classList.toggle("box1Clicked");
}

function toggleCardDetail(cardId) {
    const card = document.getElementById(cardId);
    const idIndex = cardId.substring(4);
    const detailPanelId = 'detail-panel-' + idIndex;
    console.log('Card ID:', cardId);
    console.log('Detail Panel ID:', detailPanelId);
    const detailPanel = document.getElementById(detailPanelId);
    if (!detailPanel) {
        console.error('Detail panel not found with ID:', detailPanelId);
        return;
    }
    const isActive = card.classList.contains('detail-active');
    const allDetailPanels = document.querySelectorAll('.detail-panel');
    allDetailPanels.forEach(panel => {
        panel.style.display = 'none';
    });
    const allCards = document.querySelectorAll('.detail-active');
    allCards.forEach(activeCard => {
        activeCard.classList.remove('detail-active');
    });
    if (!isActive) {
        card.classList.add('detail-active');
        detailPanel.style.display = 'block';
        // Position the detail panel relative to the clicked card const cardRect = card.getBoundingClientRect(); const cardTransform = card.getAttribute('transform'); if (cardTransform) { // Extract the Y position from the transform attribute const translateMatch = cardTransform.match(/translate\(0,(\d+)\)/); if (translateMatch && translateMatch[1]) { const cardY = parseInt(translateMatch[1]); // Position the detail panel just below the card // The card height is 140px, so we position the panel at cardY + 160 detailPanel.setAttribute('transform', 'translate(0,' + (cardY + 160) + ')'); } } } } function toggleDarkMode() { document.body.classList.toggle('dark-mode'); const elements = document.querySelectorAll('.auto-dark-text, .auto-dark-bg, .auto-dark-stroke, .auto-dark-circle, .ios-bg, .ios-card-bg, .ios-text, .ios-secondary-text'); elements.forEach(el => { el.classList.toggle('dark-active'); }); }
